import React from 'react';

import FormLayout from '../../pages/form-layout/form-layout.component'
import CustomButton from '../button/button.component'
import { withRouter } from 'react-router-dom';
import { auth } from '../../firebase/firebase.utils'

import './contact-form.styles.scss'

class ContactForm extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            displayName: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();

        const { email, password } = this.state;

        try {
            await auth.signInWithEmailAndPassword(email, password)
            this.setState({ email: '' })
        } catch(error) {
            console.log(error)
        }

        this.setState( { email: '', password: ''} )
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value })
    }

    render () {
        const { history } = this.props;
        return (
            <div className='contact-us'>
                <h1> Contact Us </h1> 
                <h2> I would love to receive more news and updates! </h2>
                <span> Sign up with your name and email. </span>

                <form onSubmit={ this.handleSubmit }>
                    <FormLayout   type='text'
                        name='displayName'
                        value={ this.displayName }
                        onChange={this.handleChange}
                        label='Display your name'
                        required
                        /> 
                    <FormLayout name="email" 
                        type="email" 
                        value={ this.state.email } 
                        label="email"
                        handleChange={ this.handleChange }
                        required/>   
                    <div className='buttons'>
                        <CustomButton type='submit' value='Submit Form' onClick={() => {
                            history.push('/thankyou')
                        }}> Submit Form </CustomButton>
                    </div>         
                </form> 
            </div>
        )
    }

}

export default withRouter(ContactForm);
