import React from 'react';

import FormLayout from '../../pages/form-layout/form-layout.component'
import CustomButton from '../button/button.component'
import { withRouter } from 'react-router-dom';
import { auth, createSubscriptionPage } from '../../firebase/firebase.utils'

import './contact-form.styles.scss'

class ContactForm extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            displayName: '',
            email: '',
            password: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();

        const { displayName, email, password } = this.state;

        try {
          const { user } = await auth.createUserWithEmailAndPassword(
            email,
            password
          );

          await createSubscriptionPage(user, { displayName });

          this.setState({
            displayName: '',
            email: '',
            password: ''
          });
        } catch (error) {
          console.error(error);
        }
      };

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value })
    }

    render () {
        const { history } = this.props;
        return (
            <div className='contact-us'>
                <h1> Contact Us </h1> 
                <h2> I would love to receive more news and updates! </h2>
                <span> Sign up with your name and email. </span>

                <form onSubmit={ this.handleSubmit }>
                    <FormLayout name="displayName" 
                        type="text" 
                        value={ this.state.displayName } 
                        label="name"
                        handleChange={ this.handleChange }
                        required/>
                    <FormLayout name="email" 
                        type="email" 
                        value={ this.state.email } 
                        label="email"
                        handleChange={ this.handleChange }
                        required/>
                    <FormLayout
                        type='password'
                        name='password'
                        value={this.state.password}
                        onChange={this.handleChange}
                        label='Password'
                        required
                        />   
                    <div className='buttons'>
                        <CustomButton type='submit' value='Submit Form' onClick={() => {
                            history.push('/thankyou')
                        }}> Submit Form </CustomButton>
                    </div>         
                </form> 
            </div>
        )
    }

}

export default withRouter(ContactForm);
